import React from "react";
import "./style.css";

export default class Card extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isMouseOver: false
    };
  }
  onHover() {
    this.setState({
      isMouseOver: !this.state.isMouseOver
    });
  }
  render() {
    return (
      <wired-card elevation="2" onMouseOver={this.onHover}>
        <img src={this.props.data.img} />
      </wired-card>
    );
  }
}
